// src/components/OneBox.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OneBox.css';

const token = 'your_jwt_token_here'; // Replace with your actual JWT token
const cookie = 'connect.sid=s%3A...'; // your actual cookie

function OneBox() {
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userName, setUserName] = useState('User');
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('threads'); // only one tab for now

  useEffect(() => {
    const fetchThreads = async () => {
      try {
        const res = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list', {
          headers: {
            Authorization: `Bearer ${token}`,
            Cookie: cookie
          },
          withCredentials: true
        });
        setThreads(res.data.data || []);
        try {
          const payload = JSON.parse(atob(token.split('.')[1]));
          setUserName(payload?.user?.firstName || 'User');
        } catch {
          setUserName('User');
        }
      } catch (err) {
        setError('Failed to fetch threads');
      } finally {
        setLoading(false);
      }
    };
    fetchThreads();
  }, []);

  return (
    <div className="onebox-container">
      <aside className="sidebar">
        <h2>OneBox</h2>
        <div className="user-label">Hi, {userName} ðŸ‘‹</div>
        <nav>
          <button
            className={activeTab === 'threads' ? 'active' : ''}
            onClick={() => setActiveTab('threads')}
          >
            Thread List
          </button>
          {/* Placeholder for more tabs */}
          {/* <button onClick={() => setActiveTab('sent')}>Sent Items</button> */}
        </nav>
      </aside>

      <main className="main-content">
        <h3>{activeTab === 'threads' ? 'All Email Threads' : ''}</h3>
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error-text">{error}</p>
        ) : (
          <>
            {activeTab === 'threads' && <ThreadList threads={threads} />}
          </>
        )}
      </main>
    </div>
  );
}

// ThreadList component to display threads
function ThreadList({ threads }) {
  return (
    <div className="threads-list">
      {threads.map((thread) => (
        <div key={thread.id} className="thread-card">
          <p><strong>{thread.subject || 'No Subject'}</strong></p>
          <p>{thread.snippet}</p>
        </div>
      ))}
    </div>
  );
}

export default OneBox;
