import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, useParams, useNavigate } from 'react-router-dom';

// Main OneBox Component
function OneBox() {
  const [emailThreads, setEmailThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userName, setUserName] = useState('User');

  const token = localStorage.getItem('token'); // Must be set after login

  useEffect(() => {
    const fetchEmailThreads = async () => {
      try {
        const response = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.data && response.data.data) {
          setEmailThreads(response.data.data);
        } else {
          setError('No threads available.');
        }

        // Try to parse user name from token if it's a JWT
        try {
          const base64Payload = token.split('.')[1];
          const payload = JSON.parse(atob(base64Payload));
          setUserName(payload.name || 'User');
        } catch {
          setUserName('User');
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching email threads:', err);
        setError('Failed to fetch email threads.');
        setLoading(false);
      }
    };

    fetchEmailThreads();
  }, [token]);

  return (
    <div>
      <h2>Good Morning, {userName}!</h2>
      {loading ? (
        <p>Loading email threads...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <Routes>
          <Route path="/onebox" element={<EmailThreads threads={emailThreads} />} />
          <Route path="/onebox/thread/:id" element={<EmailThreadDetail emailThreads={emailThreads} />} />
          <Route path="/onebox/reply/:id" element={<ReplyEmail emailThreads={emailThreads} />} />
        </Routes>
      )}
    </div>
  );
}

// List all threads
function EmailThreads({ threads }) {
  const navigate = useNavigate();

  return (
    <div>
      <h3>All Email Threads</h3>
      {threads.map((thread) => (
        <div key={thread.id} style={{ border: '1px solid black', padding: '8px', marginBottom: '8px' }}>
          <p><strong>Subject:</strong> {thread.subject}</p>
          <p><strong>Snippet:</strong> {thread.snippet}</p>
          <button onClick={() => navigate(`/onebox/thread/${thread.id}`)}>View</button>
          <button onClick={() => navigate(`/onebox/reply/${thread.id}`)}>Reply</button>
        </div>
      ))}
    </div>
  );
}

// Show one thread detail
function EmailThreadDetail({ emailThreads }) {
  const { id } = useParams();
  const thread = emailThreads.find(thread => thread.id === parseInt(id));

  return (
    <div>
      <h3>Thread Details</h3>
      {thread ? (
        <>
          <p><strong>Subject:</strong> {thread.subject}</p>
          <p><strong>Body:</strong> {thread.body}</p>
        </>
      ) : (
        <p>Thread not found.</p>
      )}
    </div>
  );
}

// Reply to a thread
function ReplyEmail({ emailThreads }) {
  const { id } = useParams();
  const thread = emailThreads.find(thread => thread.id === parseInt(id));
  const [replyBody, setReplyBody] = useState('');
  const token = localStorage.getItem('token');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!thread) return;

    const replyData = {
      toName: "Mitrajit",
      to: "chandra.rupam@gmail.com",
      from: "mitrajit2022@gmail.com",
      fromName: "Mitrajit",
      subject: `Re: ${thread.subject}`,
      body: replyBody,
      references: thread.references || [],
      inReplyTo: thread.inReplyTo || ""
    };

    try {
      await axios.post(`https://hiring.reachinbox.xyz/api/v1/onebox/reply/${thread.id}`, replyData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Reply sent successfully!');
    } catch (error) {
      console.error('Error sending reply:', error);
      alert('Failed to send reply.');
    }
  };

  return (
    <div>
      <h3>Reply to Thread</h3>
      {thread ? (
        <form onSubmit={handleSubmit}>
          <textarea
            value={replyBody}
            onChange={(e) => setReplyBody(e.target.value)}
            placeholder="Write your reply..."
            rows="5"
            cols="50"
          />
          <br />
          <button type="submit">Send Reply</button>
        </form>
      ) : (
        <p>Thread not found.</p>
      )}
    </div>
  );
}

export default OneBox;
