// src/components/OneBox.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './OneBox.css';
import ThreadDeletion from './ThreadDeletion';

function OneBox() {
  const [threads, setThreads] = useState([]);
  const [selectedThread, setSelectedThread] = useState(null);
  const [threadMessages, setThreadMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [messageLoading, setMessageLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeComponent, setActiveComponent] = useState('threadList');
  const [searchThreadId, setSearchThreadId] = useState('');
  const [showReplyBox, setShowReplyBox] = useState(false);
  const [replyText, setReplyText] = useState('');
  const [notification, setNotification] = useState(null);
  const replyBoxRef = useRef(null);

  const token = 'YOUR_VALID_JWT_TOKEN_HERE';

  useEffect(() => {
    const fetchThreads = async () => {
      try {
        const response = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setThreads(response.data.data);
      } catch (err) {
        setError('Failed to fetch threads');
      } finally {
        setLoading(false);
      }
    };
    fetchThreads();
  }, []);

  const fetchThreadMessages = async (threadId) => {
    setMessageLoading(true);
    try {
      const response = await axios.get(`https://hiring.reachinbox.xyz/api/v1/onebox/messages/${threadId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setThreadMessages(response.data.data);
      const thread = threads.find(t => t.threadId === threadId);
      setSelectedThread(thread || { threadId });
      setActiveComponent('threadMessages');
    } catch (err) {
      setError('Failed to fetch messages');
    } finally {
      setMessageLoading(false);
    }
  };

  const handleThreadSelect = (thread) => {
    setSelectedThread(thread);
    fetchThreadMessages(thread.threadId);
  };

  const handleSendReply = (e) => {
    e.preventDefault();
    console.log(`Reply to ${selectedThread.threadId}: ${replyText}`);
    setReplyText('');
    setShowReplyBox(false);
  };

  const renderActiveComponent = () => {
    if (loading) return <p>Loading threads...</p>;
    if (error) return <p className="error">{error}</p>;

    switch (activeComponent) {
      case 'threadList':
        return (
          <>
            <h2>Email Threads</h2>
            {threads.length === 0 ? (
              <p>No threads available</p>
            ) : (
              <div className="threads-list">
                {threads.map((thread) => (
                  <div
                    key={thread.threadId}
                    className="thread-card"
                    onClick={() => handleThreadSelect(thread)}
                  >
                    <h3>{thread.subject || 'No Subject'}</h3>
                    <p>{thread.body?.slice(0, 80)}...</p>
                  </div>
                ))}
              </div>
            )}
          </>
        );

      case 'threadMessages':
        return (
          <>
            <button onClick={() => setActiveComponent('threadList')}>‚Üê Back</button>
            <h2>{selectedThread?.subject || 'Thread Messages'}</h2>

            <ThreadDeletion
              thread={selectedThread}
              token={token}
              onDeleteStart={(thread) => setNotification({ type: 'info', message: `Deleting "${thread.subject}"...` })}
              onDeleteSuccess={(thread) => {
                setNotification({ type: 'success', message: `Deleted "${thread.subject}"` });
                setThreads(prev => prev.filter(t => t.threadId !== thread.threadId));
                setActiveComponent('threadList');
              }}
              onDeleteError={(err) => {
                setNotification({ type: 'error', message: err.message });
              }}
              showConfirmation={true}
            />

            {showReplyBox && (
              <form onSubmit={handleSendReply}>
                <textarea
                  value={replyText}
                  ref={replyBoxRef}
                  onChange={(e) => setReplyText(e.target.value)}
                  placeholder="Type your reply..."
                />
                <button type="submit">Send</button>
              </form>
            )}

            {messageLoading ? (
              <p>Loading messages...</p>
            ) : (
              <div className="messages-list">
                {threadMessages.map((msg, idx) => (
                  <div key={idx} className="message-card">
                    <p><strong>From:</strong> {msg.fromName || msg.fromEmail}</p>
                    <p><strong>Subject:</strong> {msg.subject}</p>
                    <div>{msg.body}</div>
                  </div>
                ))}
              </div>
            )}
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div className="onebox">
      <aside className="sidebar">
        <h3>OneBox</h3>
        <ul>
          <li onClick={() => setActiveComponent('threadList')}>Threads</li>
        </ul>
      </aside>
      <main className="main">
        {notification && <div className={`notification ${notification.type}`}>{notification.message}</div>}
        {renderActiveComponent()}
      </main>
    </div>
  );
}

export default OneBox;
