import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OneBox.css';

function OneBox() {
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Use the correct token that works in other components
  const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImVtYWlsIjoibW9udXJhbmphbjI1MUBnbWFpbC5jb20iLCJpZCI6MTU3OSwiZmlyc3ROYW1lIjoiQmlub2QiLCJsYXN0TmFtZSI6IkJoYWkifSwiaWF0IjoxNzQ2OTgwNTkyLCJleHAiOjE3Nzg1MTY1OTJ9.DrALXAi4YICQv0USd6W0S8f4yCleWM2z5pLSM1MaE-0';

  // Make the API call when the component mounts
  useEffect(() => {
    const fetchThreads = async () => {
      try {
        console.log("Fetching threads..."); // Debug log
        const response = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list', {
          headers: {
            Authorization: `Bearer ${token}`, // Using the correct token
          },
          withCredentials: true, // Adding this as it's used in ThreadList.js
        });
        
        // Debug log for response
        console.log("API Response:", response);

        if (response.data && response.data.data) {
          setThreads(response.data.data); // Assuming the response contains a 'data' field with threads
        } else {
          setError('No threads found.');
        }
      } catch (err) {
        console.error('Error fetching threads:', err);
        setError('Failed to fetch threads.');
      } finally {
        setLoading(false);
      }
    };

    fetchThreads();
  }, []);

  // Loading or error states
  if (loading) return <p>Loading threads...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="onebox-container">
      <h2>Email Threads</h2>
      {threads.length === 0 ? (
        <p>No threads available.</p>
      ) : (
        <ThreadList threads={threads} />
      )}
    </div>
  );
}

// Subcomponent to display threads
function ThreadList({ threads }) {
  return (
    <div className="threads-list">
      {threads.map((thread) => (
        <div key={thread.id} className="thread-card">
          <h3>{thread.subject || 'No Subject'}</h3>
          <p>{thread.body ? thread.body.substring(0, 100) : 'No body available'}</p>
          <p><strong>From:</strong> {thread.fromName || 'Unknown'}</p>
          <p><strong>To:</strong> {thread.toEmail || 'Unknown'}</p>
          <p><strong>Thread ID:</strong> {thread.threadId}</p>
        </div>
      ))}
    </div>
  );
}

export default OneBox;
