// src/components/OneBox.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Routes, Route, Link, useNavigate, useParams } from 'react-router-dom';
import './OneBox.css';

const token = 'your_jwt_token_here';
const cookie = 'connect.sid=s%3A...'; // your cookie

function OneBox() {
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userName, setUserName] = useState('User');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchThreads = async () => {
      try {
        const res = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list', {
          headers: {
            Authorization: `Bearer ${token}`,
            Cookie: cookie
          },
          withCredentials: true
        });
        setThreads(res.data.data || []);
        try {
          const payload = JSON.parse(atob(token.split('.')[1]));
          setUserName(payload?.user?.firstName || 'User');
        } catch {
          setUserName('User');
        }
      } catch (err) {
        setError('Failed to fetch threads');
      } finally {
        setLoading(false);
      }
    };
    fetchThreads();
  }, []);

  return (
    <div className="onebox-container">
      <aside className="sidebar">
        <h2>Inbox</h2>
        <nav>
          <Link to="/onebox">Threads</Link>
        </nav>
      </aside>

      <main className="main-content">
        <header>
          <h3>Good Morning, {userName}!</h3>
        </header>

        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : (
          <Routes>
            <Route path="/onebox" element={<EmailThreads threads={threads} />} />
            <Route path="/onebox/thread/:id" element={<ThreadDetail threads={threads} />} />
            <Route path="/onebox/reply/:id" element={<ReplyEmail threads={threads} />} />
          </Routes>
        )}
      </main>
    </div>
  );
}

function EmailThreads({ threads }) {
  const navigate = useNavigate();
  return (
    <div>
      <h4>All Threads</h4>
      {threads.map((thread) => (
        <div key={thread.id} className="thread-card">
          <p><strong>{thread.subject}</strong></p>
          <p>{thread.snippet}</p>
          <button onClick={() => navigate(`/onebox/thread/${thread.id}`)}>View</button>
          <button onClick={() => navigate(`/onebox/reply/${thread.id}`)}>Reply</button>
        </div>
      ))}
    </div>
  );
}

function ThreadDetail({ threads }) {
  const { id } = useParams();
  const thread = threads.find(t => t.id === parseInt(id));
  return thread ? (
    <div>
      <h4>{thread.subject}</h4>
      <p>{thread.body}</p>
    </div>
  ) : <p>Thread not found.</p>;
}

function ReplyEmail({ threads }) {
  const { id } = useParams();
  const thread = threads.find(t => t.id === parseInt(id));
  const [body, setBody] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!thread) return;
    const data = {
      toName: "Receiver Name",
      to: "receiver@example.com",
      from: "your@email.com",
      fromName: "Your Name",
      subject: `Re: ${thread.subject}`,
      body,
      references: thread.references || [],
      inReplyTo: thread.inReplyTo || ""
    };
    try {
      await axios.post(`https://hiring.reachinbox.xyz/api/v1/onebox/reply/${thread.id}`, data, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Reply sent!');
    } catch {
      alert('Failed to send reply');
    }
  };

  return thread ? (
    <form onSubmit={handleSubmit}>
      <h4>Reply to: {thread.subject}</h4>
      <textarea value={body} onChange={e => setBody(e.target.value)} rows="5" />
      <br />
      <button type="submit">Send</button>
    </form>
  ) : <p>Thread not found.</p>;
}

export default OneBox;
