import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Use axios for API requests

function OneBox() {
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the list of threads on component mount
    axios.get('/onebox/list')
      .then(response => {
        setThreads(response.data); // Store threads in state
        setLoading(false); // Set loading to false once data is fetched
      })
      .catch(error => {
        console.error('Error fetching threads:', error);
        setLoading(false);
      });
  }, []);

  // Delete thread functionality
  const deleteThread = (thread_id) => {
    axios.delete(`/onebox/${thread_id}`)
      .then(response => {
        setThreads(threads.filter(thread => thread.id !== thread_id)); // Remove deleted thread from state
      })
      .catch(error => {
        console.error('Error deleting thread:', error);
      });
  };

  // Render loading or threads
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>OneBox</h2>
      {threads.length === 0 ? (
        <p>No threads available.</p>
      ) : (
        threads.map(thread => (
          <div key={thread.id}>
            <h3>{thread.subject}</h3>
            <button onClick={() => deleteThread(thread.id)}>Delete</button>
            <button onClick={() => console.log('Reply box opened for:', thread.id)}>Reply</button>
          </div>
        ))
      )}
    </div>
  );
}

export default OneBox;
