import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, useParams } from 'react-router-dom';

// Main OneBox Component
function OneBox() {
  const [emailThreads, setEmailThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch email threads from the API
  useEffect(() => {
    const fetchEmailThreads = async () => {
      try {
        const response = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list');
        // Handle data if present
        if (response.data && response.data.data) {
          setEmailThreads(response.data.data);
        } else {
          setError('No threads available.');
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching email threads:', error);
        setError('Failed to fetch email threads.');
        setLoading(false);
      }
    };

    fetchEmailThreads();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading email threads...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <Routes>
          <Route
            path="/onebox"
            element={<EmailThreads threads={emailThreads} />}
          />
          <Route
            path="/onebox/thread/:id"
            element={<EmailThreadDetail emailThreads={emailThreads} />}
          />
          <Route
            path="/onebox/reply/:id"
            element={<ReplyEmail emailThreads={emailThreads} />}
          />
        </Routes>
      )}
    </div>
  );
}

// Component to display all email threads
function EmailThreads({ threads }) {
  return (
    <div>
      {threads.length === 0 ? (
        <p>No email threads available.</p>
      ) : (
        threads.map((thread) => (
          <div key={thread.id}>
            <h3>{thread.subject}</h3>
            <p>{thread.snippet}</p>
            <p>Thread ID: {thread.id}</p>
          </div>
        ))
      )}
    </div>
  );
}

// Component to display email thread details
function EmailThreadDetail({ emailThreads }) {
  const { id } = useParams();
  const thread = emailThreads.find(thread => thread.id === parseInt(id));

  return (
    <div>
      <h2>{thread ? thread.subject : 'Thread Not Found'}</h2>
      <p>{thread ? thread.body : 'No details available.'}</p>
    </div>
  );
}

// Component to reply to an email thread
function ReplyEmail({ emailThreads }) {
  const { id } = useParams();
  const thread = emailThreads.find(thread => thread.id === parseInt(id));
  const [replyBody, setReplyBody] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const replyData = {
      toName: "Mitrajit",
      to: "chandra.rupam@gmail.com",
      from: "mitrajit2022@gmail.com",
      fromName: "Mitrajit",
      subject: `Re: ${thread.subject}`,
      body: replyBody,
      references: [thread.id],
      inReplyTo: thread.id
    };

    try {
      await axios.post(`https://hiring.reachinbox.xyz/api/v1/onebox/reply/${thread.id}`, replyData);
      alert('Replied to thread successfully!');
    } catch (error) {
      console.error('Error replying to email:', error);
      alert('Failed to reply to the thread.');
    }
  };

  return (
    <div>
      <h3>Reply to: {thread ? thread.subject : 'Thread Not Found'}</h3>
      <form onSubmit={handleSubmit}>
        <textarea
          value={replyBody}
          onChange={(e) => setReplyBody(e.target.value)}
          placeholder="Write your reply..."
        />
        <button type="submit">Send Reply</button>
      </form>
    </div>
  );
}

export default OneBox;
