import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OneBox.css';

function OneBox() {
  const [emailThreads, setEmailThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmailThreads = async () => {
      try {
        const response = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list');
        if (response.data) {
          setEmailThreads(response.data);
        } else {
          setError('No email threads found.');
        }
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch data from the API.');
        setLoading(false);
      }
    };
    fetchEmailThreads();
  }, []);

  const handleDeleteThread = async (threadId) => {
    try {
      await axios.delete(`https://hiring.reachinbox.xyz/api/v1/onebox/messages/${threadId}`);
      setEmailThreads(emailThreads.filter(thread => thread.id !== threadId));
    } catch (error) {
      console.error('Error deleting email thread:', error);
    }
  };

  const handleReply = async (threadId) => {
    const replyData = {
      toName: "Mitrajit",
      to: "chandra.rupam@gmail.com",
      from: "mitrajit2022@gmail.com",
      fromName: "Mitrajit",
      subject: "Optimize Your Recruitment Efforts with Expert Support",
      body: "<p>Hello how are you</p>",
      references: [
        "<dea5a0c2-336f-1dc3-4994-191a0ad3891a@gmail.com>",
        "<CAN5Dvwu24av80BmEg9ZVDWaP2+hTOrBQn9KhjfFkZZX_Do88FA@mail.gmail.com>",
        "<CAN5DvwuzPAhoBEpQGRUOFqZF5erXc=B98Ew_5zbHF5dmeKWZMQ@mail.gmail.com>",
        "<a1383d57-fdee-60c0-d46f-6bc440409e84@gmail.com>"
      ],
      inReplyTo: "<a1383d57-fdee-60c0-d46f-6bc440409e84@gmail.com>"
    };

    try {
      await axios.post(`https://hiring.reachinbox.xyz/api/v1/onebox/reply/${threadId}`, replyData);
      alert('Replied to thread successfully!');
    } catch (error) {
      console.error('Error replying to email:', error);
    }
  };

  return (
    <div className="onebox-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        <div className="content-area">
          {loading ? (
            <p>Loading email threads...</p>
          ) : error ? (
            <p>{error}</p>
          ) : (
            <EmailThreads
              threads={emailThreads}
              handleDeleteThread={handleDeleteThread}
              handleReply={handleReply}
            />
          )}
        </div>
      </div>
    </div>
  );
}

function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        <li>Dashboard</li>
        <li>Messages</li>
        <li>Settings</li>
        <li>Logout</li>
      </ul>
    </div>
  );
}

function Header() {
  return (
    <div className="header">
      <h1>OneBox Dashboard</h1>
    </div>
  );
}

function EmailThreads({ threads, handleDeleteThread, handleReply }) {
  return (
    <div className="email-threads">
      {threads.map((thread) => (
        <div className="email-thread" key={thread.id}>
          <h3>{thread.subject}</h3>
          <p>{thread.snippet}</p>
          <button onClick={() => handleDeleteThread(thread.id)}>Delete</button>
          <button onClick={() => handleReply(thread.id)}>Reply</button>
        </div>
      ))}
    </div>
  );
}

export default OneBox;
