import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OneBox.css';

// Main OneBox Component
function OneBox() {
  const [emailThreads, setEmailThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch email threads on component mount
  useEffect(() => {
    const fetchEmailThreads = async () => {
      try {
        console.log('Fetching email threads...');
        const response = await axios.get(
          'https://hiring.reachinbox.xyz/api/v1/onebox/list'
        );
        console.log('API Response:', response);

        if (response.data) {
          setEmailThreads(response.data);
        } else {
          setError('No email threads found.');
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching email threads', error);
        setError('Failed to fetch data from the API.');
        setLoading(false);
      }
    };
    fetchEmailThreads();
  }, []);

  return (
    <div className="onebox-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        <div className="content-area">
          {loading ? (
            <p>Loading email threads...</p>
          ) : error ? (
            <p>{error}</p>
          ) : (
            <EmailThreads threads={emailThreads} />
          )}
        </div>
      </div>
    </div>
  );
}

// Sidebar Component
function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        <li>Dashboard</li>
        <li>Messages</li>
        <li>Settings</li>
        <li>Logout</li>
      </ul>
    </div>
  );
}

// Header Component
function Header() {
  return (
    <div className="header">
      <h1>OneBox Dashboard</h1>
    </div>
  );
}

// Email Threads Component
function EmailThreads({ threads }) {
  return (
    <div className="email-threads">
      {threads.map((thread) => (
        <div className="email-thread" key={thread.id}>
          <h3>{thread.subject}</h3>
          <p>{thread.snippet}</p>
        </div>
      ))}
    </div>
  );
}

export default OneBox;
