import React, { useState, useEffect } from 'react';
import './OneBox.css'; // Create this CSS file for custom styles

function OneBox() {
  const [apiData, setApiData] = useState(null);

  // Example API call, replace this with your actual API
  useEffect(() => {
    // Simulating an API fetch
    setTimeout(() => {
      setApiData({ message: 'Data fetched from the API!' });
    }, 2000);
  }, []);

  return (
    <div className="onebox-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        <div className="content-area">
          {/* Display API data here */}
          {apiData ? (
            <p>{apiData.message}</p>
          ) : (
            <p>Loading data from the API...</p>
          )}
        </div>
      </div>
    </div>
  );
}

// Sidebar component
function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        <li>Dashboard</li>
        <li>Messages</li>
        <li>Settings</li>
        <li>Logout</li>
      </ul>
    </div>
  );
}

// Header component
function Header() {
  return (
    <div className="header">
      <h1>OneBox Dashboard</h1>
    </div>
  );
}

export default OneBox;
