import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate, Routes, Route, useParams } from 'react-router-dom';
import './OneBox.css';

function OneBox() {
  const [emailThreads, setEmailThreads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmailThreads = async () => {
      try {
        const response = await axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list');
        if (response.data) {
          setEmailThreads(response.data);
        } else {
          setError('No email threads found.');
        }
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch data from the API.');
        setLoading(false);
      }
    };
    fetchEmailThreads();
  }, []);

  const handleDeleteThread = async (threadId) => {
    try {
      await axios.delete(`https://hiring.reachinbox.xyz/api/v1/onebox/messages/${threadId}`);
      setEmailThreads(emailThreads.filter(thread => thread.id !== threadId));
    } catch (error) {
      console.error('Error deleting email thread:', error);
    }
  };

  const handleReply = async (threadId, replyData) => {
    try {
      await axios.post(`https://hiring.reachinbox.xyz/api/v1/onebox/reply/${threadId}`, replyData);
      alert('Replied to thread successfully!');
      navigate('/onebox');
    } catch (error) {
      console.error('Error replying to email:', error);
    }
  };

  return (
    <div className="onebox-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        <div className="content-area">
          {loading ? (
            <p>Loading email threads...</p>
          ) : error ? (
            <p>{error}</p>
          ) : (
            <Routes>
              <Route
                path="/onebox"
                element={<EmailThreads threads={emailThreads} handleDeleteThread={handleDeleteThread} />}
              />
              <Route
                path="/onebox/thread/:id"
                element={<EmailThreadDetail emailThreads={emailThreads} />}
              />
              <Route
                path="/onebox/reply/:id"
                element={<ReplyEmail emailThreads={emailThreads} handleReply={handleReply} />}
              />
            </Routes>
          )}
        </div>
      </div>
    </div>
  );
}

function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        <li><Link to="/onebox">All Threads</Link></li>
        <li><Link to="/onebox/thread/1">View Thread</Link></li>
        <li><Link to="/onebox/reply/1">Reply to Thread</Link></li>
      </ul>
    </div>
  );
}

function Header() {
  return (
    <div className="header">
      <h1>OneBox Dashboard</h1>
    </div>
  );
}

function EmailThreads({ threads, handleDeleteThread }) {
  return (
    <div className="email-threads">
      {threads.map((thread) => (
        <div className="email-thread" key={thread.id}>
          <h3>{thread.subject}</h3>
          <p>{thread.snippet}</p>
          <button onClick={() => handleDeleteThread(thread.id)}>Delete</button>
          <Link to={`/onebox/thread/${thread.id}`}>View Thread</Link>
          <Link to={`/onebox/reply/${thread.id}`}>Reply</Link>
        </div>
      ))}
    </div>
  );
}

function EmailThreadDetail({ emailThreads }) {
  const { id } = useParams();
  const thread = emailThreads.find(thread => thread.id === parseInt(id));

  return (
    <div className="email-thread-detail">
      <h2>{thread ? thread.subject : 'Thread Not Found'}</h2>
      <p>{thread ? thread.body : 'No details available.'}</p>
      <Link to="/onebox">Back to Threads</Link>
    </div>
  );
}

function ReplyEmail({ emailThreads, handleReply }) {
  const { id } = useParams();
  const thread = emailThreads.find(thread => thread.id === parseInt(id));

  const [replyBody, setReplyBody] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const replyData = {
      toName: "Mitrajit",
      to: "chandra.rupam@gmail.com",
      from: "mitrajit2022@gmail.com",
      fromName: "Mitrajit",
      subject: `Re: ${thread.subject}`,
      body: replyBody,
      references: [thread.id],
      inReplyTo: thread.id
    };

    handleReply(thread.id, replyData);
  };

  return (
    <div className="reply-email">
      <h3>Reply to: {thread ? thread.subject : 'Thread Not Found'}</h3>
      <form onSubmit={handleSubmit}>
        <textarea
          value={replyBody}
          onChange={(e) => setReplyBody(e.target.value)}
          placeholder="Write your reply..."
        />
        <button type="submit">Send Reply</button>
      </form>
      <Link to="/onebox">Back to Threads</Link>
    </div>
  );
}

export default OneBox;
